import React, { useState } from 'react'
import { v4 as uuidv4 } from 'uuid'
import toast from 'react-hot-toast'
const Home = () => {
    const [roomId, setRoomId] = useState('');
    const [Username, setUsername] = useState('');
    const createNewRoom = (e) => {
        e.preventDefault();
        const id = uuidv4();
        setRoomId(id);
        toast.success('New room created')
    }

    return (
        <div className='homepage'>
            <div className='form'>
                <img src='/logo1.png' className='logo' />
                <h4 className='roomidlabel'>Enter the RoomId</h4>
                <div className='inputroomid'>
                    <input type='text' className='input' placeholder='Room ID' onChange={(e) => setRoomId(e.target.value)} value={roomId} />
                    <input type='text' className='input' placeholder='Username' onChange={(e) => setUsername(e.target.value)} value={Username} />
                    <button className='btn joinbtn'>Join</button>
                    <span className='createRoom'>
                        Don't have a Room ID? Create one &nbsp;
                        <a onClick={createNewRoom} href='' className='newroomidbtn'>New Room</a>
                    </span>

                </div>
            </div>
            <footer>
                <h4>Built Simply by {''}
                    <a href='https://github.com/amannsinghhh'>AmannSinghhh</a></h4>
            </footer>

        </div>
    )
}

export default Home
