import React, { useState, useEffect, useRef } from 'react';
import { Controlled as CodeMirror } from 'react-codemirror2';
import io from 'socket.io-client';

import 'codemirror/lib/codemirror.css';
import 'codemirror/theme/material.css';

const Editor = ({ roomID }) => {
    const [code, setCode] = useState('');
    const socketRef = useRef();

    useEffect(() => {
        // Connect to the server's socket.io instance
        socketRef.current = io.connect('http://your-server-url'); // Replace with your actual server URL

        // Join the room
        socketRef.current.emit('joinRoom', roomID);

        // Listen for code updates from the server
        socketRef.current.on('codeUpdate', (newCode) => {
            setCode(newCode);
        });

        return () => {
            // Disconnect when component unmounts
            socketRef.current.disconnect();
        };
    }, [roomID]);

    // Handle code changes and emit updates to the server
    const handleCodeChange = (editor, data, newCode) => {
        setCode(newCode);
        socketRef.current.emit('updateCode', newCode);
    };

    return (
        <div className="editor-container">
            <CodeMirror
                value={code}
                onBeforeChange={handleCodeChange}
                options={{
                    mode: 'javascript', // Set your preferred language mode
                    theme: 'material',  // Set your preferred theme
                    lineNumbers: true,
                }}
            />
        </div>
    );
};

export default Editor;
