import React from "react";
import CodeMirror from "@uiw/react-codemirror";
import { vscodeDark } from "@uiw/codemirror-theme-vscode";
import { javascript } from '@codemirror/lang-javascript';
import { EditorState } from '@codemirror/state';
import { highlightActiveLine, drawSelection, bracketMatching, syntaxHighlighting, closeBrackets, highlightSpecialChars, autocompletion, indentOnInput } from '@codemirror/language';

const Editor = () => {
    const code = "console.log('Start Writing!');"
    const editorState = EditorState.create({
        doc: initialCode,
        extensions: [
            basicSetup,
            oneDark, // Theme
            javascript(), // Language mode
            highlightActiveLine(),
            drawSelection(),
            bracketMatching(),
            syntaxHighlighting(),
            closeBrackets(),
            highlightSpecialChars(),
            autocompletion(),
            indentOnInput(),
        ],
    });
    return (
        <CodeMirror
            value={code}
            height="700px"
            theme={vscodeDark}
            options={{
                mode: 'javascript',
                theme: 'material',
                lineNumbers: true,
            }}
        />
    );
}
export default Editor;




