import React, { useRef, useEffect } from "react";
import { useState } from "react";
import CodeMirror from "@uiw/react-codemirror";
import { vscodeDark } from "@uiw/codemirror-theme-vscode";
import { javascript } from '@codemirror/lang-javascript';
import { EditorState, Transaction } from '@codemirror/state';
import axios from "axios";
import io from "socket.io-client"; // Import the WebSocket library

const Editor = () => {
    const editorRef = useRef(null);
    const [code, setCode] = useState("console.log('Start Writing!');");
    const [output, setOutput] = useState("");
    const [socket, setSocket] = useState(null);

    useEffect(() => {
        // Connect to the WebSocket server
        const newSocket = io("YOUR_WEBSOCKET_SERVER_URL");
        setSocket(newSocket);

        // Cleanup on unmount
        return () => {
            newSocket.disconnect();
        };
    }, []);

    useEffect(() => {
        if (!socket) return;

        // Handle incoming changes from other users
        socket.on('change', (changes) => {
            applyChangesToCodeMirror(changes);
        });
    }, [socket]);

    const applyChangesToCodeMirror = (changes) => {
        const { changes: cmChanges } = changes;
        if (editorRef.current) {
            const state = editorRef.current.state;
            const tr = new Transaction(state).replaceRange(
                cmChanges.from,
                cmChanges.to,
                cmChanges.text.join('\n')
            );
            editorRef.current.dispatch(tr);
        }
    };

    const runCode = async () => {
        try {
            setOutput("Running...");

            const response = await axios.post(
                "https://runkit.io/api/run/1.0.0",
                { code },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );

            setOutput(response.data);
        } catch (error) {
            setOutput("Error: " + error.message);
        }
    };

    const handleChange = (editor, data, value) => {
        // Send changes to the server
        if (socket) {
            socket.emit('change', { changes: data });
        }
        setCode(value);
    };

    return (
        <div>
            <CodeMirror
                ref={editorRef}
                value={code}
                height="650px"
                theme={vscodeDark}
                options={{
                    mode: { name: javascript(), json: true },
                    theme: 'material',
                    lineNumbers: true,
                    autocompletion: true,
                    closeBrackets: true,
                }}
                onChange={handleChange}
            />
            <button className="btn runbtn" onClick={runCode}>Run</button>
            <h2 className="outputtxt">Output:</h2>
            <pre>{output}</pre>
        </div>
    );
};

export default Editor;
