import React, { useEffect, useRef } from "react";
import CodeMirror from "@uiw/react-codemirror";
import { vscodeDark } from "@uiw/codemirror-theme-vscode";
import { EditorState } from "@codemirror/state";
import { basicSetup, autocompletion, closeBrackets } from "@codemirror/basic-setup"; // Updated imports
import io from "socket.io-client"; // Imported socket.io client library
import ACTIONS from "../action";

const Editor = ({ roomID }) => {
    const editorRef = useRef(null);
    const socketRef = useRef(null); // Added socket reference
    const [code, setCode] = useState("console.log('Start Writing!');");
    const [output, setOutput] = useState("");

    const runCode = async () => {
        try {
            setOutput("Running...");

            const response = await axios.post(
                "https://runkit.io/api/run/1.0.0",
                { code },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );

            setOutput(response.data);
        } catch (error) {
            setOutput("Error: " + error.message);
        }
    };

    useEffect(() => {
        // Connect to WebSocket server
        socketRef.current = io("YOUR_SOCKET_SERVER_URL");

        // Handle incoming code changes from other users
        socketRef.current.on(ACTIONS.CODE_CHANGE, ({ code }) => {
            editorRef.current.dispatch({
                changes: { from: 0, to: editorRef.current.state.doc.length, insert: code },
            });
        });

        return () => {
            // Disconnect from WebSocket server when component unmounts
            socketRef.current.disconnect();
        };
    }, []);

    return (
        <div>
            <CodeMirror
                value={code}
                height="650px"
                theme={vscodeDark}
                options={{
                    ...EditorState.tabSize.of(2),
                    ...basicSetup,
                    autocompletion: autocompletion(),
                    closeBrackets: closeBrackets(),
                }}
                onChange={(editor, data, value) => {
                    setCode(value);
                    socketRef.current.emit(ACTIONS.CODE_CHANGE, {
                        roomID,
                        code: value,
                    });
                }}
            />
            <button className="btn runbtn" onClick={runCode}>
                Run
            </button>
            <h2 className="outputtxt">Output:</h2>
            <pre>{output}</pre>
        </div>
    );
};

export default Editor;
