import React, { useEffect, useRef } from "react";
import { useState } from "react";
import CodeMirror from "@uiw/react-codemirror";
import { autocompletion, closeBrackets, closeBracketsKeymap, completionKeymap } from '@codemirror/autocomplete'
import { vscodeDark } from "@uiw/codemirror-theme-vscode";
import { javascript } from '@codemirror/lang-javascript';
import { EditorState } from '@codemirror/state';
import axios from "axios";
import ACTIONS from "../action";
import io from "socket.io-client"; // Imported socket.io client library
import { basicSetup } from "codemirror"; // Updated imports

const Editor = ({ roomID }) => {
    const editorRef = useRef(null);
    const socketRef = useRef(null);
    const [code, setCode] = useState("console.log('Start Writing!');");
    const [output, setOutput] = useState("");

    const runCode = async () => {
        try {
            setOutput("Running...");

            const response = await axios.post(
                "https://runkit.io/api/run/1.0.0",
                { code },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );

            setOutput(response.data);
        } catch (error) {
            setOutput("Error: " + error.message);
        }
    };

    useEffect(() => {
        socketRef.current = io("YOUR_SOCKET_SERVER_URL");

        socketRef.current.on(ACTIONS.CODE_CHANGE, ({ code }) => {
            if (editorRef.current) {
                const currentCode = editorRef.current.editor.doc.toString();
                if (currentCode !== code) {
                    editorRef.current.editor.doc.replace(0, editorRef.current.editor.doc.length, code);
                }
            }
        });

        return () => {
            socketRef.current.disconnect();
        };
    }, []);

    const handleCodeChange = (editor, data, value) => {
        setCode(value);
        socketRef.current.emit(ACTIONS.CODE_CHANGE, {
            roomID,
            code: value,
        });
    };

    return (
        <div>
            <CodeMirror
                ref={editorRef}
                value={code}
                height="650px"
                theme={vscodeDark}
                options={{
                    ...EditorState.tabSize.of(2),
                    ...basicSetup,
                    autocompletion: autocompletion(),
                    closeBrackets: closeBrackets(),
                }}
                onBeforeChange={handleCodeChange} // Changed from `onChange`
            />
            <button className="btn runbtn" onClick={runCode}>
                Run
            </button>
            <h2 className="outputtxt">Output:</h2>
            <pre>{output}</pre>
        </div>
    );
};

export default Editor;
