import { EditorState, EditorView, basicSetup } from "@codemirror/basicsetup";
import { javascript } from "@codemirror/lang-javascript";

useEffect(() => {
    if (!element) return;

    const view = new EditorView({
        state: EditorState.create({
            extensions: [basicSetup, javascript()],
        }),
        parent: element,
    });

    return () => view.destroy();
}, [element]);
