import React, { useEffect, useRef } from "react";
import { useState } from "react";
import CodeMirror from "@uiw/react-codemirror";
import { autocompletion, closeBrackets, closeBracketsKeymap, completionKeymap } from '@codemirror/autocomplete'
import { vscodeDark } from "@uiw/codemirror-theme-vscode";
import { javascript } from '@codemirror/lang-javascript';
import { EditorState } from '@codemirror/state';
import axios from "axios";
import ACTIONS from "../action";



const Editor = ({ socketRef, roomID }) => {
    const editorRef = useRef(null);
    const [code, setCode] = useState("console.log('Start Writing!');");
    const [output, setOutput] = useState("");

    useEffect(() => {
        // Attach the socket event listener for code changes
        socketRef.current.on(ACTIONS.CODE_CHANGE, ({ code }) => {
            if (code !== null) {
                setCode(code); // Update the local code state
                editorRef.current.editor.setValue(code); // Update the editor content
            }
        });

        return () => {
            socketRef.current.off(ACTIONS.CODE_CHANGE);
        };
    }, [socketRef.current]);

    const handleChange = (editor, data, value) => {
        setCode(value);
        socketRef.current.emit(ACTIONS.CODE_CHANGE, {
            roomID,
            code: value,
        });
    };

    const runCode = async () => {
        try {
            setOutput("Running...");

            const response = await axios.post(
                "https://runkit.io/api/run/1.0.0",
                { code },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );

            setOutput(response.data);
        } catch (error) {
            setOutput("Error: " + error.message);
        }
    };

    return (
        <div>
            <CodeMirror
                ref={editorRef} // Assign the ref here
                value={code}
                height="650px"
                theme={vscodeDark}
                options={{
                    mode: javascript(),
                    theme: 'material',
                    lineNumbers: true,
                    autocompletion: true,
                    autocloseBrackets: true,
                }}
                onChange={handleChange} // Use the handleChange function
            />
            <button className="btn runbtn" onClick={runCode}>Run</button>
            <h2 className="outputtxt">Output:</h2>
            <pre>{output}</pre>
        </div>
    );
};

export default Editor;
