import React, { useEffect, useRef, useState } from "react";
import CodeMirror from "@uiw/react-codemirror";
import { vscodeDark } from "@uiw/codemirror-theme-vscode";
import { javascript } from "@codemirror/lang-javascript";
import { EditorState } from "@codemirror/state";
import ACTIONS from "../action";

const Editor = ({ socketRef, roomID }) => {
    const editorRef = useRef(null);
    const [code, setCode] = useState("console.log('Start Writing!');");
    const [output, setOutput] = useState("");

    useEffect(() => {
        if (socketRef.current) {
            socketRef.current.on(ACTIONS.CODE_CHANGE, ({ code }) => {
                if (code !== null) {
                    editorRef.current.editor.setValue(code); // Update the editor content
                }
            });
        }

        editorRef.current.on("change", (instance, changes) => {
            console.log("changes", changes);
            const { origin } = changes;
            const newCode = instance.getValue();
            setCode(newCode);
            if (origin !== "setValue") {
                socketRef.current.emit(ACTIONS.CODE_CHANGE, {
                    roomID,
                    code: newCode,
                });
            }
        });
    }, []);

    const runCode = async () => {
        try {
            setOutput("Running...");

            // ...rest of your code

        } catch (error) {
            setOutput("Error: " + error.message);
        }
    };

    return (
        <div>
            <CodeMirror
                ref={editorRef} // Assign the ref here
                value={code}
                height="650px"
                theme={vscodeDark}
                options={{
                    mode: { name: javascript, json: true }, // Remove unnecessary ()
                    theme: "material",
                    lineNumbers: true,
                    autocompletion: true,
                    autocloseBrackets: true,
                }}
                onChange={(editor, data, value) => {
                    setCode(value);
                }}
            />
            <button className="btn runbtn" onClick={runCode}>
                Run
            </button>
            <h2 className="outputtxt">Output:</h2>
            <pre>{output}</pre>
        </div>
    );
};

export default Editor;
